                managerEmail = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_EMAIL;
                managersName = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_NAME;
            }
            else if (formOwner.EmployeeManagementTier == 3)
            {
                managerEmail = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_EMAIL;
                managersName = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_NAME;
            }
            else
            {
                foreach (var reqManager in reqManagers)
                {
                    managerEmail += reqManager.EmployeeEmail + ";";
                    if (managersName == "")
                        managersName += await GetFullName(reqManager.EmployeeEmail);
                    else
                        managersName += "," + await GetFullName(reqManager.EmployeeEmail);
                }

            }

            

            var ccRecipients = responseData.additionalNotificationRecipients;

            StringBuilder ccNotification = new StringBuilder();

            foreach (var ccRecipient in ccRecipients)
            {
                ccNotification.Append(ccRecipient.EmployeeEmail.ToString());
                ccNotification.Append(";");
                var additionalNotifyRecips = await GetAdfUserByEmail(ccRecipient.EmployeeEmail);
                await SetAdHocPermission(formInfo.FormInfoId, null, additionalNotifyRecips.EmployeePositionId);
            }

            cc = ccNotification.ToString().TrimEnd(';');

            foreach (var emailType in workflow.EmailNotificationModel.EmailSendType)
            {
                var emailReceiver = new List<string>();
                bool isNextApprover = false;
                bool isRecalled = false;

                switch (emailType)
                {
                    //2.0 Manager Review - Task Notification to Requestor's Manager	- NotificationToRequestorsManager
                    //2.0.1 Manager Review - Reminder task notification to Requestor's Manager - ReminderNotificationToRequestorsManager
                    case EmailSendType.RequestorManager:
                        subject = $"Sea Going Allowance Claim eForm #{formInfo.FormInfoId} has been submitted for your review";
                        body = string.Format(SeaGoingAllowanceClaimsTemplate.NotificationToRequestorsManager
                        , formOwnerFullName
                        , formInfo.FormInfoId
                        , responseData.dateAllowanceClaims?.ToString("dd/MM/yyyy")
                        , responseData.startdateSeaGoingAllowanceClaim?.ToString("dd/MM/yyyy")
                        , responseData.enddateSeaGoingAllowanceClaim?.ToString("dd/MM/yyyy")
                        , responseData.totaldaysSeaGoingAllowanceClaim
                        , responseData.allowancetype
                        , workflow.EmailNotificationModel.SummaryUrl);
                        isNextApprover = true;
                        break;


                    //2.0.2 Form Recalled - Task Notification to Requestor - RecalledNotificationToRequestor
                    case EmailSendType.Recalled:
                        subject = $"Sea Going Allowance Claim eForm #{formInfo.FormInfoId} has been recalled";
                        body = string.Format(SeaGoingAllowanceClaimsTemplate.Recalled
                            , formOwnerFullName
                            , formInfo.FormInfoId
                            , workflow.EmailNotificationModel.SummaryUrl);
                        emailReceiver.Add(formInfo.FormOwnerEmail);
                        isRecalled = true;
                        break;

                    //2.2 Form Rejected - Manager Reject Notification to Requestor
                    case EmailSendType.Rejected:
                        subject = $"Sea Going Allowance Claim eForm #{formInfo.FormInfoId} has been rejected";
                        body = string.Format(SeaGoingAllowanceClaimsTemplate.Rejected
                            , formOwnerFullName
                            , actionByFullName
                            , formInfo.FormInfoId
                            , workflow.FormInfoRequest.ReasonForRejection
                            , workflow.EmailNotificationModel.SummaryUrl);
                        emailReceiver.Add(formInfo.FormOwnerEmail);
                        break;

                    //2.3 Task Escalation - Task Notification to Requestor's Line Manager's Manager
                    //2.3.1 Task Escalation - Reminder Task Notification to Requestor's Manager's Manager
                    case EmailSendType.Escalated:
                        subject = $"Sea Going Allowance Claim eForm #{formInfo.FormInfoId} has escalated for your review";
                        body = string.Format(SeaGoingAllowanceClaimsTemplate.Escalated
                        , formOwnerFullName
                        , formInfo.FormInfoId                        
                        , workflow.EmailNotificationModel.SummaryUrl);
                        isNextApprover = true;
                        break;

                    //2.5 Task Escalation - Task Notification to PODeFormsBusinessAdmins Group
                    case EmailSendType.EscalationNotificationToPODeFormsBusinessAdminsGroup:
                        subject = $"Sea Going Allowance Claim eForm #{formInfo.FormInfoId} has escalated for your action";
                        body = string.Format(SeaGoingAllowanceClaimsTemplate.EscalationNotificationToPODeFormsBusinessAdminsGroup
                            , formOwnerFullName
                            , formInfo.FormInfoId
                            , workflow.EmailNotificationModel.SummaryUrl);
                        isNextApprover = true;
                        break;

                    //2.6 Task Delegation - Task Notification to Delegated Manager
                    case EmailSendType.Delegated:
                        subject = $"Sea Going Allowance Claim eForm #{formInfo.FormInfoId} has been delegated for your review";
                        body = string.Format(SeaGoingAllowanceClaimsTemplate.Delegated
                        , formOwnerFullName
                        , formInfo.FormInfoId                        
                        , workflow.EmailNotificationModel.SummaryUrl);
                        isNextApprover = true;
                        break;

                    //3.0 Form Approved - Manager Approve Notification to Requestor
                    case EmailSendType.ManagerApproveNotificationToRequestor:
                        subject = $"Sea Going Allowance Claim eForm #{formInfo.FormInfoId} has been approved";
                        body = string.Format(SeaGoingAllowanceClaimsTemplate.ManagerApproveNotificationToRequestor
                            , formOwnerFullName
                            , actionByFullName
                            , formInfo.FormInfoId
                            , workflow.EmailNotificationModel.SummaryUrl);
                        emailReceiver.Add(formInfo.FormOwnerEmail);
                        break;

                    
                    //4.0 Completion Notification - Completion Notification to Requestor
                    case EmailSendType.Completed:
                        subject = $"Sea Going Allowance Claim eForm #{formInfo.FormInfoId} has been processed";
                        body = string.Format(SeaGoingAllowanceClaimsTemplate.CompletionNotificationToRequestor
                        , formOwnerFullName
                        , formInfo.FormInfoId
                        , responseData.dateAllowanceClaims?.ToString("dd/MM/yyyy")
                        , responseData.startdateSeaGoingAllowanceClaim?.ToString("dd/MM/yyyy")
                        , responseData.enddateSeaGoingAllowanceClaim?.ToString("dd/MM/yyyy")
                        , responseData.totaldaysSeaGoingAllowanceClaim
                        , responseData.allowancetype
                        , workflow.EmailNotificationModel.SummaryUrl);
                        emailReceiver.Add(formInfo.FormOwnerEmail);
                        break;
                }

                if (isNextApprover)
                {
                    if (workflow.FormInfoRequest.NextApproverEmail != AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_EMAIL)
                    {
                        //var nextApproverPosition = await GetAdfUserById(workflow.FormInfoRequest.NextApproverEmail);
                        var approvers = await GetAdfUserByEmail(workflow.FormInfoRequest.NextApproverEmail);
                        var position = await GetAdfUserByPositionId(approvers.EmployeePositionId ?? 0);
                        emailReceiver = position.Select(x => x.EmployeeEmail).ToList();
                    }
                    else
                    {
                        if (formOwner.EmployeeManagementTier == 1)
                        {
                            managerEmail = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_EMAIL;
                            emailReceiver.Add(managerEmail);
                            managersName = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_NAME;
                        }
                        else if (formOwner.EmployeeManagementTier == 2)
                        {
                            managerEmail = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_EMAIL;
                            emailReceiver.Add(managerEmail);
                            managersName = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_NAME;
                        }
                        else if (formOwner.EmployeeManagementTier == 3)
                        {
                            managerEmail = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_EMAIL;
                            emailReceiver.Add(managerEmail);
                            managersName = AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_NAME;
                        }
                    }



                }

                if (isRecalled)
                {
                    cc = managerEmail;
                }

                if (workflow.FormInfoRequest.AllowReminder)
                {
                    await UpdateTasksAsync(formInfo, workflow.FormInfoRequest.AllowEscalation, 2, 3);
                }

                if (!string.IsNullOrEmpty(body))
                {
                    if (emailType == EmailSendType.Completed)
                    {
                        await _formEmailService.SendEmail(formInfo.FormInfoId
                                , formInfo.FormOwnerEmail
                                , subject
                                , body
                                , cc);
                    }
                    else if (emailType == EmailSendType.EscalationNotificationToPODeFormsBusinessAdminsGroup)
                    {
                        await _formEmailService.SendEmail(formInfo.FormInfoId
                                , AllowanceAndClaims.POD_EFFORMS_BUSINESS_ADMIN_GROUP_EMAIL
                                , subject
                                , body
                                , cc);
                    }
                    else
                    {
                        foreach (var email in emailReceiver)
                        {
                            await _formEmailService.SendEmail(formInfo.FormInfoId
                                    , email
                                    , subject
                                    , body
                                    , cc);
                        }
                    }
                }
            }
        }

