        //additional hours claims
        public async Task<int> AdditionalHoursClaimsWorkFlow(FormInfoRequest additionalHoursClaimsFormInfo)
        {
            try
            {

                var workFlow = new WorkflowModel();
                var formInfo = new FormInfo();
                List<int?> adHocPermissionEmployees = new List<int?>();

                dynamic responseData;


                if (additionalHoursClaimsFormInfo?.Response != null)
                {
                    responseData = JsonConvert.DeserializeObject<AdditionalHoursClaimsModel>(additionalHoursClaimsFormInfo.Response);
                }


                var formInfoExisting = additionalHoursClaimsFormInfo.FormId == 0 ? null : await _formInfoRepository.FirstOrDefaultAsync(x => x.FormInfoId == additionalHoursClaimsFormInfo.FormId);

                var formOwner = formInfoExisting == null
                    ? await GetAdfUserByEmail(additionalHoursClaimsFormInfo.FormOwnerEmail)
                    : await GetAdfUserByEmail(formInfoExisting.FormOwnerEmail);

                switch (additionalHoursClaimsFormInfo.StatusId)
                {
                    case (int)FormStatus.Submitted when formOwner.EmployeeManagementTier == 2
                                      || (!formOwner.Managers.Any() && formOwner.EmployeeManagementTier != 4):
                        workFlow = await AssignToPTSTeam(additionalHoursClaimsFormInfo);
                        break;
                    case (int)FormStatus.Submitted:
                        workFlow = await AdditionalHoursClaimsSubmitted(additionalHoursClaimsFormInfo);
                        break;
                    case (int)FormStatus.Delegated:
                        workFlow = await AdditionalHoursClaimsDelegated(additionalHoursClaimsFormInfo);
                        break;
                    case (int)FormStatus.Unsubmitted:
                    case (int)FormStatus.Completed:
                        additionalHoursClaimsFormInfo.FormOwnerEmail = additionalHoursClaimsFormInfo.ActionBy;
                        workFlow = SetFormDefaultClaimsForms(additionalHoursClaimsFormInfo);
                        break;
                    case (int)FormStatus.Approved:
                        workFlow = await AdditionalHoursClaimsApproved(additionalHoursClaimsFormInfo);
                        break;
                }
               



                formInfo = workFlow.FormInfoRequest.FormId == 0 || workFlow.FormInfoRequest.FormId == null
                                ? await CreateFormInfo(workFlow.FormInfoRequest)
                                : await UpdateFormInfo(workFlow.FormInfoRequest);

                await SetButton(formInfo, workFlow);

                foreach (int? user in adHocPermissionEmployees)
                {
                    await SetAdHocPermission(formInfo.FormInfoId, null, user);
                }


                await AdditionalHoursClaimsSendEmail(workFlow, formInfo);
                return additionalHoursClaimsFormInfo.ActionBy == additionalHoursClaimsFormInfo.FormOwnerEmail
                    ? formInfo.FormInfoId
                    : 0;
            }
            catch
            {
                return 0;
            }
        }

