        public async Task<JsonResult> AdditionalHoursClaimsApproval(FormInfoRequest additionalHoursClaimsFormInfo)
        {
            var result = new JsonResult(null);
            try
            {
                List<int> formInfoIds = new List<int>();
                List<string> formOwnersLst = new List<string>();
                

                var formInfoExisting = await _formInfoRepository.FirstOrDefaultAsync(x => x.FormInfoId == additionalHoursClaimsFormInfo.FormId);
                if (formInfoExisting != null)
                {
                    var responseData = JsonConvert.DeserializeObject<AdditionalHoursClaimsModel>(formInfoExisting.Response);
                }
                else
                {
                    var responseData = JsonConvert.DeserializeObject<AdditionalHoursClaimsModel>(additionalHoursClaimsFormInfo.Response);
                }
                

                var actualStatus = additionalHoursClaimsFormInfo.StatusId;
                formOwnersLst.Add(additionalHoursClaimsFormInfo.ActionBy);
                additionalHoursClaimsFormInfo.AllowPersonalInfo = formOwnersLst.Count == 1;                
                foreach (string formOwners in formOwnersLst)
                {
                    additionalHoursClaimsFormInfo.FormOwnerEmail = formOwners;
                    additionalHoursClaimsFormInfo.StatusId = actualStatus;
                    formInfoIds.Add(await AdditionalHoursClaimsWorkFlow(additionalHoursClaimsFormInfo));
                }

                formInfoIds = formInfoIds.Where(x => x != 0).ToList();
                result.Value = new
                {
                    outcome = "Success",
                    formInfoId = formInfoIds.Any() ? formInfoIds.First() : 0,
                };
                result.StatusCode = StatusCodes.Status200OK;
                return result;
            }
            catch (Exception ex)
            {
                result.Value = new
                {
                    outcome = "Error: Bad request, update your form."
                };
                result.StatusCode = StatusCodes.Status400BadRequest;
                return result;
            }
        }

