                List<int?> adHocPermissionEmployees = new List<int?>();



                dynamic responseData;


                if (motorvehicleallowanceclaimsFormInfo?.Response != null)
                {
                    responseData = JsonConvert.DeserializeObject<MotorVehicleAllowanceClaimsModel>(motorvehicleallowanceclaimsFormInfo.Response);
                }

                var formInfoExisting = motorvehicleallowanceclaimsFormInfo.FormId == 0 ? null : await _formInfoRepository.FirstOrDefaultAsync(x => x.FormInfoId == motorvehicleallowanceclaimsFormInfo.FormId);

                var formOwner = formInfoExisting == null
                    ? await GetAdfUserByEmail(motorvehicleallowanceclaimsFormInfo.FormOwnerEmail)
                    : await GetAdfUserByEmail(formInfoExisting.FormOwnerEmail);

                switch (motorvehicleallowanceclaimsFormInfo.StatusId)
                {
