                        break;
                    case (int)FormStatus.Unsubmitted:
                    case (int)FormStatus.Completed:
                        motorvehicleallowanceclaimsFormInfo.FormOwnerEmail = motorvehicleallowanceclaimsFormInfo.ActionBy;
                        workFlow = SetFormDefaultClaimsForms(motorvehicleallowanceclaimsFormInfo);
                        break;
                    case (int)FormStatus.Approved:
                        workFlow = await MotorVehicleAllowanceClaimsApproved(motorvehicleallowanceclaimsFormInfo);
                        break;
                }


                formInfo = workFlow.FormInfoRequest.FormId == 0 || workFlow.FormInfoRequest.FormId == null
                                ? await CreateFormInfo(workFlow.FormInfoRequest)
                                : await UpdateFormInfo(workFlow.FormInfoRequest);

                await SetButton(formInfo, workFlow);

                foreach (int? user in adHocPermissionEmployees)
                {
                    await SetAdHocPermission(formInfo.FormInfoId, null, user);
                }

                await MotorVehicleAllowanceClaimsSendEmail(workFlow, formInfo);
                return motorvehicleallowanceclaimsFormInfo.ActionBy == motorvehicleallowanceclaimsFormInfo.FormOwnerEmail
                    ? formInfo.FormInfoId
                    : 0;
            }
            catch
            {
                return 0;
            }
        }

