        {
            try
            {
                var workFlow = new WorkflowModel();
                var formInfo = new FormInfo();
                List<int?> adHocPermissionEmployees = new List<int?>();



                dynamic responseData;


                if (outofhourscontactclaimsFormInfo?.Response != null)
                {
                    responseData = JsonConvert.DeserializeObject<OutofHoursContactClaimsModel>(outofhourscontactclaimsFormInfo.Response);
                }

                var formInfoExisting = outofhourscontactclaimsFormInfo.FormId == 0 ? null : await _formInfoRepository.FirstOrDefaultAsync(x => x.FormInfoId == outofhourscontactclaimsFormInfo.FormId);

                var formOwner = formInfoExisting == null
                    ? await GetAdfUserByEmail(outofhourscontactclaimsFormInfo.FormOwnerEmail)
                    : await GetAdfUserByEmail(formInfoExisting.FormOwnerEmail);

                switch (outofhourscontactclaimsFormInfo.StatusId)
                {
                    case (int)FormStatus.Submitted when formOwner.EmployeeManagementTier == 2
                                      || (!formOwner.Managers.Any() && formOwner.EmployeeManagementTier != 4):
                        workFlow = await AssignToPTSTeam(outofhourscontactclaimsFormInfo);
                        break;
                    case (int)FormStatus.Submitted:
                        workFlow = await OutOfHoursContactClaimsSubmitted(outofhourscontactclaimsFormInfo);
                        break;
                    case (int)FormStatus.Delegated:
                        workFlow = await OutOfHoursContactClaimsDelegated(outofhourscontactclaimsFormInfo);
                        break;
                    case (int)FormStatus.Unsubmitted:
                    case (int)FormStatus.Completed:
                        outofhourscontactclaimsFormInfo.FormOwnerEmail = outofhourscontactclaimsFormInfo.ActionBy;
                        workFlow = SetFormDefaultClaimsForms(outofhourscontactclaimsFormInfo);
                        break;
                    case (int)FormStatus.Approved:
                        workFlow = await OutOfHoursContactClaimsApproved(outofhourscontactclaimsFormInfo);
                        break;
                }


                formInfo = workFlow.FormInfoRequest.FormId == 0 || workFlow.FormInfoRequest.FormId == null
                                ? await CreateFormInfo(workFlow.FormInfoRequest)
                                : await UpdateFormInfo(workFlow.FormInfoRequest);

                await SetButton(formInfo, workFlow);

                foreach (int? user in adHocPermissionEmployees)
                {
                    await SetAdHocPermission(formInfo.FormInfoId, null, user);
                }

                await OutofHoursContactClaimsSendEmail(workFlow, formInfo);
                return outofhourscontactclaimsFormInfo.ActionBy == outofhourscontactclaimsFormInfo.FormOwnerEmail
                    ? formInfo.FormInfoId
                    : 0;
            }
            catch
            {
                return 0;
            }
        }

